import{_ as a}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.65c6b98c.js";import{_ as r,o as l,c as o,k as i,a as n,E as s,O as c}from"./chunks/framework.c989bd33.js";import"./chunks/commonjsHelpers.725317a4.js";const k=JSON.parse('{"title":"CIS105: Computer Applications & Information Systems Lect. 11","description":"","frontmatter":{},"headers":[],"relativePath":"academic/cis105/cis105-l11-lecture-note.md","filePath":"academic/cis105/cis105-l11-lecture-note.md","lastUpdated":1726418622000}'),u={name:"academic/cis105/cis105-l11-lecture-note.md"};function d(h,e,m,p,f,y){const t=a;return l(),o("div",null,[e[0]||(e[0]=i("h1",{id:"cis105-computer-applications-information-systems-lect-11",tabindex:"-1"},[n("CIS105: Computer Applications & Information Systems Lect. 11 "),i("a",{class:"header-anchor",href:"#cis105-computer-applications-information-systems-lect-11","aria-label":'Permalink to "CIS105: Computer Applications & Information Systems Lect. 11"'},"​")],-1)),s(t,{readTime:"2",words:"406"}),e[1]||(e[1]=c('<h2 id="chapter-11-computer-security" tabindex="-1">Chapter 11: Computer Security <a class="header-anchor" href="#chapter-11-computer-security" aria-label="Permalink to &quot;Chapter 11: Computer Security&quot;">​</a></h2><blockquote><p>Midterm reminder: March 28th - 29th</p><ul><li>Exam Review after Chapter 11KC</li><li>40 new questions, 10 KC questions <ul><li>Same format as last time</li></ul></li></ul></blockquote><h3 id="_11-1-system-security-computer-privacy" tabindex="-1">11.1: System Security &amp; Computer Privacy <a class="header-anchor" href="#_11-1-system-security-computer-privacy" aria-label="Permalink to &quot;11.1: System Security &amp; Computer Privacy&quot;">​</a></h3><ul><li>An unsecured computer is extremely easy to breach by any seasoned hacker</li><li>Computer Security: Concerned with risk management, confidentiality, integrity, and the availability of the electronic info processed and stored within a computing system</li><li>Risk Management: Includes the recognition, consequences, and assessment of risk to a computer&#39;s assets, and developing strategies to manage and protect them</li></ul><h3 id="_11-2-business-system-threats" tabindex="-1">11.2: Business System Threats <a class="header-anchor" href="#_11-2-business-system-threats" aria-label="Permalink to &quot;11.2: Business System Threats&quot;">​</a></h3><ul><li>Intranet: A businesses&#39; private version of the internet</li><li>The key aspect of an intranet is privacy</li><li>Firewall: Hardware and software working together to ensure that only authorized personnel can access the business&#39; intranet</li></ul><h3 id="_11-3-firewall" tabindex="-1">11.3: Firewall <a class="header-anchor" href="#_11-3-firewall" aria-label="Permalink to &quot;11.3: Firewall&quot;">​</a></h3><ul><li>Without a firewall, an intranet is just another part of the internet</li><li>Proxy Server: A special security computer that allows communication to flow in and out of an intranet to check for external threats</li><li>Two standard security practices for firewalls: <ul><li>Default-Deny</li><li>Default-Allow</li></ul></li></ul><h3 id="_11-4-malware-threats" tabindex="-1">11.4: Malware Threats <a class="header-anchor" href="#_11-4-malware-threats" aria-label="Permalink to &quot;11.4: Malware Threats&quot;">​</a></h3><ul><li>The term &quot;virus&quot; comes from biology</li><li>Malware Motivations: <ul><li>Experimental Malware</li><li>Worms</li><li>Trojan Horses</li><li>Spyware/Keylogging</li><li>Adware</li><li>Spamming</li><li>Denial-of-Service Attach (DoS) <ul><li>The deadliest</li></ul></li><li>Reverse Phishing - &quot;Spear Phishing&quot;</li></ul></li></ul><h3 id="_11-5-malware-solutions" tabindex="-1">11.5: Malware Solutions <a class="header-anchor" href="#_11-5-malware-solutions" aria-label="Permalink to &quot;11.5: Malware Solutions&quot;">​</a></h3><ul><li>Antivirus Software <ul><li>Norton AntiVirus</li><li>McAfee&#39;s VirusScan</li></ul></li><li>System patches</li><li>Data Backup</li><li>Employee training</li></ul><h3 id="_11-6-internet-fraud" tabindex="-1">11.6: Internet Fraud <a class="header-anchor" href="#_11-6-internet-fraud" aria-label="Permalink to &quot;11.6: Internet Fraud&quot;">​</a></h3><ul><li>Internet fraud: Any fraudulent activity in an online setting</li><li>Google AdSense, Google&#39;s advertising network; the middleman between the advertiser and the website owner</li><li>Click Fraud: a program that automatically clicks on Ads</li><li>Purchase Scam: scammer asks seller to pay for shipping via credit card, cancels credit card after order has shipped</li><li>Most uncomplicated type of internet fraud</li><li>Phishing: Uses social engineering techniques to gain private information</li></ul><h3 id="_11-7-computer-privacy" tabindex="-1">11.7: Computer Privacy <a class="header-anchor" href="#_11-7-computer-privacy" aria-label="Permalink to &quot;11.7: Computer Privacy&quot;">​</a></h3><ul><li>Privacy: The ability of an individual to keep their personal information out of public view</li><li>Anonymous: One&#39;s personal identity Is not be known</li><li>Tracking Cookie: A small text file that tracks your interaction on a website</li><li>History files: A list of stored or cached websites on a user&#39;s computer</li></ul><blockquote><p>50% of the time an unsecured computer can be hacked, and a password can be guessed</p></blockquote><h3 id="_11-8-identity-theft" tabindex="-1">11.8: Identity Theft <a class="header-anchor" href="#_11-8-identity-theft" aria-label="Permalink to &quot;11.8: Identity Theft&quot;">​</a></h3><ul><li>Identity theft: A crime concerning the unlawful practice of assuming another individual&#39;s identity</li><li>Ways to acquire personal information: <ul><li>Shoulder Surfing</li><li>Dumpster Diving</li></ul></li><li>SCAM <ul><li>Stingy</li><li>Check</li><li>Ask</li><li>Maintain</li></ul></li><li>ASU&#39;s Recommended Password Manager: Stache <ul><li>Bitwarden, 1Password, Dashlane, Keeper, KeePass</li></ul></li></ul>',19))])}const S=r(u,[["render",d]]);export{k as __pageData,S as default};
