import{_ as r}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.65c6b98c.js";import{_ as i,o,c as n,k as e,a as s,E as l,O as d}from"./chunks/framework.c989bd33.js";import"./chunks/commonjsHelpers.725317a4.js";const P=JSON.parse('{"title":"CIS105: Computer Applications & Information Systems Lect. 3","description":"","frontmatter":{},"headers":[],"relativePath":"academic/cis105/cis105-l3-lecture-note.md","filePath":"academic/cis105/cis105-l3-lecture-note.md","lastUpdated":1726417910000}'),c={name:"academic/cis105/cis105-l3-lecture-note.md"};function u(p,t,h,m,b,y){const a=r;return o(),n("div",null,[t[0]||(t[0]=e("h1",{id:"cis105-computer-applications-information-systems-lect-3",tabindex:"-1"},[s("CIS105: Computer Applications & Information Systems Lect. 3 "),e("a",{class:"header-anchor",href:"#cis105-computer-applications-information-systems-lect-3","aria-label":'Permalink to "CIS105: Computer Applications & Information Systems Lect. 3"'},"​")],-1)),l(a,{readTime:"3",words:"582"}),t[1]||(t[1]=d('<h2 id="chapter-3-computer-hardware" tabindex="-1">Chapter 3: Computer Hardware <a class="header-anchor" href="#chapter-3-computer-hardware" aria-label="Permalink to &quot;Chapter 3: Computer Hardware&quot;">​</a></h2><h3 id="_3-1-technical-terminologies" tabindex="-1">3.1: Technical Terminologies <a class="header-anchor" href="#_3-1-technical-terminologies" aria-label="Permalink to &quot;3.1: Technical Terminologies&quot;">​</a></h3><ul><li><strong>System Unit</strong>: Main body of the computer that contains a motherboard.</li><li><strong>Motherboard</strong> (AKA Circuit Board): Main component of a system unit; a compleex array of electronics that connect and help different components of the computer communicate with each other. <ul><li>PC: Motherboards, Mac; Logic Boards</li></ul></li><li><strong>Chassis</strong> (AKA Case or Box): Case to enclose the main components of a computer</li><li><strong>Microprocessor</strong>: The brains of the computer <ul><li><strong>Central processing unit (CPU)</strong>: Interprets program instructions and processes data by performing arithmetic and logical operations.</li></ul></li></ul><h3 id="_3-2-central-processing-unit-cpu" tabindex="-1">3.2: Central Processing Unit (CPU) <a class="header-anchor" href="#_3-2-central-processing-unit-cpu" aria-label="Permalink to &quot;3.2: Central Processing Unit (CPU)&quot;">​</a></h3><ul><li>Speed is directly, but not solely, related to the CPU</li><li>Measured in Clock Rate <ul><li>The number of cycles per second, that a computer can perform its most basic task</li></ul></li><li>RSIC (Reduced Instruction Set Computer): Many chips encased into one chip</li><li>Bus Lines; Pathways that transfer data and power between components inside of a computer.</li></ul><table><thead><tr><th>Architecture Diagram of CPU</th><th>Actual CPU Image</th></tr></thead><tbody><tr><td><img src="https://computersciencewiki.org/images/1/1a/Cpu_diagram.png" alt="Architecture of the central processing unit (CPU) - Computer ..."></td><td><img src="https://i.insider.com/60402d8eb46d720018b04c1f?width=700" alt="What Is a CPU? a Guide to Your Computer&#39;s &#39;Brain&#39;"></td></tr></tbody></table><h3 id="_3-3-power-supply-unit-psu" tabindex="-1">3.3 Power Supply Unit (PSU) <a class="header-anchor" href="#_3-3-power-supply-unit-psu" aria-label="Permalink to &quot;3.3 Power Supply Unit (PSU)&quot;">​</a></h3><ul><li>Supplies electricity</li><li>Converts 100-120 volts or 220-240 volts of <strong>alternating current (AC)</strong> to a lower voltage direct current (DC) that can be used by the internal components of the system unit</li><li>Different currents used in different parts of the world.</li></ul><h3 id="_3-4-primary-and-secondary-storage" tabindex="-1">3.4: Primary and Secondary Storage <a class="header-anchor" href="#_3-4-primary-and-secondary-storage" aria-label="Permalink to &quot;3.4: Primary and Secondary Storage&quot;">​</a></h3><ul><li><strong>Primary</strong>: The workbench <ul><li>Random Access Memory (RAM): Primary storage</li></ul></li><li><strong>Secondary</strong>: The storage for all your tools and supplies <ul><li>Hard Drive: Secondary storage</li></ul></li><li>ROM Chips (Read-Only Memory): Preprogrammed chips that serve specialized internal tasks. No human intervention <ul><li>AKA Firmware</li></ul></li></ul><h4 id="_3-4-1-secondary-storage-contd" tabindex="-1">3.4.1: Secondary Storage Contd. <a class="header-anchor" href="#_3-4-1-secondary-storage-contd" aria-label="Permalink to &quot;3.4.1: Secondary Storage Contd.&quot;">​</a></h4><ul><li>Internal vs. External Storage</li><li>Hard Drivers vs. Solid State Drivers (SSDs)</li><li>Impractical for a computer to be stand-alone</li><li>Important to consider business needs <ul><li>Bad IT can make or break a business</li></ul></li></ul><h3 id="_3-5-binary-number-system" tabindex="-1">3.5: Binary Number System <a class="header-anchor" href="#_3-5-binary-number-system" aria-label="Permalink to &quot;3.5: Binary Number System&quot;">​</a></h3><ul><li><p>Computer only understands one language: Machine code or machine language</p></li><li><p>1 or 0 (on or off)</p></li><li><p>1s or 0s are referred to as bits (short for binary digits)</p></li><li><p>8 bits become a byte</p><ul><li>Byte 8 bits</li><li>Kilob</li></ul></li><li><p>American Standard Code for Information Interchange (ASCII): The coding scheme that most microcomputer use to represent bytes.</p><ul><li><table><thead><tr><th>Name</th><th>Equal To</th><th>Size (In Bytes)</th></tr></thead><tbody><tr><td>Bit</td><td>1 Bit</td><td>1/8</td></tr><tr><td>Nibble</td><td>4 Bits</td><td>1/2 (rare)</td></tr><tr><td>Byte</td><td>8 Bits</td><td>1</td></tr><tr><td>Kilobyte</td><td>1024 Bytes</td><td>1024</td></tr><tr><td>Megabyte</td><td>1, 024 Kilobytes</td><td>1, 048, 576</td></tr><tr><td>Gigabyte</td><td>1, 024 Megabytes</td><td>1, 073, 741, 824</td></tr><tr><td>Terrabyte</td><td>1, 024 Gigabytes</td><td>1, 099, 511, 627, 776</td></tr><tr><td>Petabyte</td><td>1, 024 Terabytes</td><td>1, 125, 899, 906, 842, 624</td></tr><tr><td>Exabyte</td><td>1, 024 Petabytes</td><td>1, 152, 921, 504, 606, 846, 976</td></tr><tr><td>Zettabyte</td><td>1, 024 Exabytes</td><td>1, 180, 591, 620, 717, 411, 303, 424</td></tr><tr><td>Yottabyte</td><td>1, 024 Zettabytes</td><td>1, 208, 925, 819, 614, 629, 174, 706, 176</td></tr></tbody></table></li></ul></li></ul><h3 id="_3-6-peripheral-devices" tabindex="-1">3.6: Peripheral Devices <a class="header-anchor" href="#_3-6-peripheral-devices" aria-label="Permalink to &quot;3.6: Peripheral Devices&quot;">​</a></h3><ul><li>Input v. Output</li><li>Input device: keywords, mice, touchpad, stylus, speakers, microphone, digital camera, etc/</li><li>Optical Character Recognition (OCR): Converting printed text to digital text</li><li>Radio Frequency Identification (RFID): An input mechanism that can be used to label a product for identification and have the product&#39;s information transmitted through radio waves.</li></ul><h3 id="_3-7-output-devices-contd" tabindex="-1">3.7: Output Devices Contd. <a class="header-anchor" href="#_3-7-output-devices-contd" aria-label="Permalink to &quot;3.7: Output Devices Contd.&quot;">​</a></h3><ul><li>Monitor: A series of transistors that translate machine code into text and images./</li><li>Pixels: A single dot on a graphic or text image</li><li>Resolution: The number of pixels inside a defined dimension on a monitor, commonly referred to as <strong>dots per inch (dpi).</strong><ul><li>Resolution is the most important feature of a monitor.</li></ul></li></ul>',18))])}const S=i(c,[["render",u]]);export{P as __pageData,S as default};
