import{_ as a}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.250b3e56.js";import{_ as n,o,c as l,H as r,k as e,a as i,Q as c}from"./chunks/framework.b7580407.js";import"./chunks/commonjsHelpers.725317a4.js";const w=JSON.parse('{"title":"CIS105: Computer Applications & Information Systems Lect. 4","description":"","frontmatter":{},"headers":[],"relativePath":"academic/cis105/cis105-l4-lecture-note.md","filePath":"academic/cis105/cis105-l4-lecture-note.md","lastUpdated":1710111963000}'),s={name:"academic/cis105/cis105-l4-lecture-note.md"},d=e("h1",{id:"cis105-computer-applications-information-systems-lect-4",tabindex:"-1"},[i("CIS105: Computer Applications & Information Systems Lect. 4 "),e("a",{class:"header-anchor",href:"#cis105-computer-applications-information-systems-lect-4","aria-label":'Permalink to "CIS105: Computer Applications & Information Systems Lect. 4"'},"​")],-1),u=c('<h2 id="chapter-4-formulas-and-functions" tabindex="-1">Chapter 4: Formulas and Functions <a class="header-anchor" href="#chapter-4-formulas-and-functions" aria-label="Permalink to &quot;Chapter 4: Formulas and Functions&quot;">​</a></h2><h3 id="_4-1-references-and-calculations" tabindex="-1">4.1: References and Calculations <a class="header-anchor" href="#_4-1-references-and-calculations" aria-label="Permalink to &quot;4.1: References and Calculations&quot;">​</a></h3><ul><li>Calculations <ul><li><strong>Add (<code>+</code>), Subtract (<code>-</code>), Multiply (<code>*</code>), Divide (<code>/</code>)</strong></li><li>Add: <code>A1+A2</code></li><li>Subtract: <code>A1-A2</code></li><li>Multiply: <code>A1*A2</code></li><li>Divide: <code>A1/A2</code></li></ul></li><li><strong>Always end typing by pressing <kbd>Enter</kbd></strong></li><li>Al formulas start with an equals (<code>=</code>) sign</li></ul><h3 id="_4-2-references-and-calculations" tabindex="-1">4.2: References and Calculations <a class="header-anchor" href="#_4-2-references-and-calculations" aria-label="Permalink to &quot;4.2: References and Calculations&quot;">​</a></h3><ul><li><p>References: When we perform calculations in Excel, we often reference the value stored in other cells in our worksheet</p></li><li><p>Three ays to reference a cell:</p><ul><li>Relative reference - <code>A1</code></li><li>Mixed reference - <code>$A1</code> or <code>A$1</code></li><li>Absolute reference - <code>$A$1</code></li></ul></li><li><p><strong>Relative Reference</strong>: An address or pointer that changes when the target item is moved or the relationship to it has changed.</p><ul><li><strong>RELATIVE REFERENCE IS THE DEFALT REFERENCE IN EXCEL</strong>.</li><li>The reference changes when the formula is copied elsewhere.</li><li>Excel sees the location of the cells relative to the location of the formula.</li><li>Click <kbd>Fn</kbd> + <kbd>F4</kbd> as hotkeys to construct value as relative value</li></ul></li><li><p><strong>Absolute Reference</strong>: Will make either the row or column &quot;constant&quot; in other words, the ABSOLUTE cell location is LOCKED</p><ul><li>To create an absolute cell reference, put a dollar sign ``$` symbol in front of the part of the reference that you want to remain constant.</li></ul></li><li><table><thead><tr><th>Reference</th><th>Comments</th></tr></thead><tbody><tr><td><code>A1</code></td><td>Both the column and row references are &quot;relative&quot; and will change when the reference is copied and pasted to other cell.</td></tr><tr><td><code>$A1</code></td><td>The column reference is &quot;<strong>absolute</strong>&quot; and will remain constant when copied and pasted to other cells. The row reference is &quot;relative&quot; and will change when copied and pasted to cells in other rows of the worksheet</td></tr><tr><td><code>A$1</code></td><td>The column reference is &quot;relative&quot; and will change when copied and pasted to cells in other columns in the worksheet. The row reference is &quot;<strong>absolute</strong>&quot; and will remain constant when copied and pasted to other cells.</td></tr><tr><td><code>$A$1</code></td><td>Both the column and row references are &quot;<strong>absolute</strong>&quot; and will remain constant when the reference is copied and pated to other cells.</td></tr></tbody></table></li></ul><h3 id="_4-3-summary-statistics-functions" tabindex="-1">4.3: Summary Statistics Functions <a class="header-anchor" href="#_4-3-summary-statistics-functions" aria-label="Permalink to &quot;4.3: Summary Statistics Functions&quot;">​</a></h3><ul><li>Formula vs. Function <ul><li>Formula: Any calculation in excel</li><li>Function: A pre-defined calculation</li></ul></li><li>To perform calculations in Excel, we often reference the values stored in other cells in our worksheets. <strong>We reference the cell location, not the value in the cell</strong>.</li><li><code>COUNT(value1, [value2]), ...)</code>: Counts the number of cells in that contain numbers.</li><li><code>COUNTA(value1, [value2], ...)</code>: Counts the number of cells in a range of cells that are not blank.</li><li><code>AVERAGE(number1, [number2], ...)</code>: Calculates the simple average of a set of numbers.</li><li><code>MAX(number1, [number2], ...)</code>: Returns the largest value in a set of numbers.</li><li><code>MIN(number1, [number2], ...)</code>: Returns the smallest value in a set of numbers.</li></ul><h3 id="_4-4-financial-function" tabindex="-1">4.4: Financial Function <a class="header-anchor" href="#_4-4-financial-function" aria-label="Permalink to &quot;4.4: Financial Function&quot;">​</a></h3><ul><li><code>RATE(nper, pmt, pv, [fv], [type], [guess])</code> : calculates the interest rate earned for an investment given the number of payments made as part of the investment, the payment amount, and the current value of the investment.</li><li><code>EFFECT(nominal _rate, pery)</code>: calculates the annual percentage rate for an interest rate given the number of times per year that interest is charged.</li><li><code>NPER(rate, pmt, pv, [fv], [type])</code>: calculates the number of payments that will be made to pay off a loan given the interest rate, payment amount, and original loan amount.</li><li><code>PMT(rate, per, pv, [fv], [type])</code>: calculates the payment amount for a loan given the interest rate, number of payments to be made to pay off the loan, and the original loan amount.</li><li><code>PV(rate, per, pmt, [fv], (type))</code>: calculates the current value (accounting for compounding interest) of an investment given the interest rate, number of payments to be made, and the amount of the payment.</li><li><code>FV(rate, per, pmt, [pv], [type])</code>: calculates the future value of an investment given the interest rate, number of payments to be made, and the amount of the payment.</li></ul>',9);function h(m,f,p,b,g,v){const t=a;return o(),l("div",null,[d,r(t,{readTime:"4",words:"643"}),u])}const E=n(s,[["render",h]]);export{w as __pageData,E as default};
