import{_ as t}from"./chunks/PageInfo.vue_vue_type_script_setup_true_lang.250b3e56.js";import{_ as a,o as r,c as l,H as s,k as e,a as o,Q as n}from"./chunks/framework.b7580407.js";import"./chunks/commonjsHelpers.725317a4.js";const v=JSON.parse('{"title":"CIS105: Computer Applications & Information Systems Lect. 9","description":"","frontmatter":{},"headers":[],"relativePath":"academic/cis105/cis105-l9-lecture-note.md","filePath":"academic/cis105/cis105-l9-lecture-note.md","lastUpdated":1711521027000}'),c={name:"academic/cis105/cis105-l9-lecture-note.md"},d=e("h1",{id:"cis105-computer-applications-information-systems-lect-9",tabindex:"-1"},[o("CIS105: Computer Applications & Information Systems Lect. 9 "),e("a",{class:"header-anchor",href:"#cis105-computer-applications-information-systems-lect-9","aria-label":'Permalink to "CIS105: Computer Applications & Information Systems Lect. 9"'},"​")],-1),u=n('<h2 id="chapter-9-internet" tabindex="-1">Chapter 9: Internet <a class="header-anchor" href="#chapter-9-internet" aria-label="Permalink to &quot;Chapter 9: Internet&quot;">​</a></h2><h3 id="_9-1-internet-history" tabindex="-1">9.1: Internet History <a class="header-anchor" href="#_9-1-internet-history" aria-label="Permalink to &quot;9.1: Internet History&quot;">​</a></h3><ul><li>1969: University of California Los Angeles (UCLA) created the Advanced Research Project Agency Network (ARPANET)</li><li>1989: Hypertext Transfer Protocol (HTTP) / World Wide Web (WWW)</li><li>1993: Hypertext Markup Langauge (HTML)</li></ul><h3 id="_9-2-internet-and-www-structure" tabindex="-1">9.2: Internet and WWW Structure <a class="header-anchor" href="#_9-2-internet-and-www-structure" aria-label="Permalink to &quot;9.2: Internet and WWW Structure&quot;">​</a></h3><ul><li>he internet is a network called the Client/Server Model</li><li>The browser application software interprets HTML and HTTP to create web pages</li><li>Everything on the internet lives somewhere physically; the trick is using computer addresses to send info from one place to another</li></ul><h3 id="_9-3-internet-addresses" tabindex="-1">9.3: Internet Addresses <a class="header-anchor" href="#_9-3-internet-addresses" aria-label="Permalink to &quot;9.3: Internet Addresses&quot;">​</a></h3><ul><li>Internet Protocol Address (IP): An address system used to identify and communicate through the network</li><li>Uniform Resource Locators (URLs) <ul><li><code>https://asu.edu</code></li><li><code>https</code>: refers to standard protocol</li><li><code>www</code>: refers to the world wide web</li><li><code>Canvas.asu</code>: domain name</li><li><code>.edu</code>: a top-level domain code or extension</li></ul></li></ul><h3 id="_9-4-9-5-websites-browsers" tabindex="-1">9.4/9.5: Websites/Browsers <a class="header-anchor" href="#_9-4-9-5-websites-browsers" aria-label="Permalink to &quot;9.4/9.5: Websites/Browsers&quot;">​</a></h3><ul><li>Website: A collection of web pages on a server that exists on the web that can be requested by a client and displayed with a browser</li><li>Hyperlink: Clickable navigation element</li><li>Browser: An application software that provides an interface for the WWW</li><li>Aspects of a good website: <ul><li>Content</li><li>Interactivity</li><li>Design</li></ul></li></ul><h3 id="_9-6-browser-features" tabindex="-1">9.6: Browser Features <a class="header-anchor" href="#_9-6-browser-features" aria-label="Permalink to &quot;9.6: Browser Features&quot;">​</a></h3><ul><li>Common Browser Features <ul><li>Bookmarks: AKA favorites</li><li>Caching: Just like a CPU cache on the internet</li><li>Cookies: Text files used to track info about users</li><li>Graphics File (GIF, JPEG): Images and videos</li><li>Javascript: A programming language <ul><li>Used to make websites dynamics; Ex: Countdown timer</li></ul></li></ul></li><li>Browser Considerations: <ul><li>Security</li><li>Popup Blockers</li><li>Usability</li></ul></li></ul><h3 id="_9-7-internet-service-providers-isp" tabindex="-1">9.7: Internet Service Providers (ISP) <a class="header-anchor" href="#_9-7-internet-service-providers-isp" aria-label="Permalink to &quot;9.7: Internet Service Providers (ISP)&quot;">​</a></h3><ul><li>Connectivity: When one computer connects to another and shares info &amp; resources</li><li>Internet Service Providers (ISP) <ul><li>Dial-up &amp; Digital Subscriber Line (DSL)</li><li>Broadband Internet Access</li><li>Web Hosting</li></ul></li></ul><h3 id="_9-8-search-engines" tabindex="-1">9.8: Search Engines <a class="header-anchor" href="#_9-8-search-engines" aria-label="Permalink to &quot;9.8: Search Engines&quot;">​</a></h3><ul><li>Search engine: A website that lets a user type in specific key words or phrases and then return a list of hyperlinks that best match the search criteria <ul><li>Google, Bing, Yahoo</li><li>Directory searches</li><li>Metasearch Engines <ul><li>Ex: Dogpile</li></ul></li></ul></li></ul><h3 id="_9-9-e-commerce" tabindex="-1">9.9: E-Commerce <a class="header-anchor" href="#_9-9-e-commerce" aria-label="Permalink to &quot;9.9: E-Commerce&quot;">​</a></h3><ul><li>Electronic Commerce (E-Commerce): The distribution, buying, selling, advertising, and marketing of goods and services over the web <ul><li>Business-to-Business (B2B)</li><li>Business-to-Consumer (B2C)</li><li>Consumer-to-Consumer (C2C)</li></ul></li><li>The rules of business still apply to online environments</li></ul><h3 id="_9-10-security" tabindex="-1">9.10: Security <a class="header-anchor" href="#_9-10-security" aria-label="Permalink to &quot;9.10: Security&quot;">​</a></h3><ul><li>Malware: Software designed to damage a computer&#39;s system without the owner&#39;s knowledge <ul><li>Virus</li><li>Worms</li><li>Trojan Horse</li><li>Spyware</li><li>Adware</li></ul></li></ul><h3 id="_9-11-9-12-email-email-security" tabindex="-1">9.11/9.12: Email/Email Security <a class="header-anchor" href="#_9-11-9-12-email-email-security" aria-label="Permalink to &quot;9.11/9.12: Email/Email Security&quot;">​</a></h3><ul><li>Email: &quot;A store and forward&quot; system of composing, sending, storing, and receiving message over electronic communication systems.</li><li>Never send an email you wouldn&#39;t be willing to say in public</li><li>Phishing: An email that looks official but is malicious in nature</li><li>Spam: Unwanted mail</li></ul>',21);function h(m,p,b,f,w,_){const i=t;return r(),l("div",null,[d,s(i,{readTime:"2",words:"426"}),u])}const P=a(c,[["render",h]]);export{v as __pageData,P as default};
